INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (2, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, 'BASIC', null, 'The perfect topper for chicken, fish, burgers or pizza. A great blend of Habanero, Mango, Passion Fruit and more make this Death Sauce an amazing tropical treat.', 'Sweet Death Sauce', 10.99000, null, null, 'Y', null, null, null, 2);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (3, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'Tangy, ripe cayenne peppes flow together with garlic, onion, tomato paste and a hint of cane sugar to make this a smooth sauce with a bite.  Wonderful on eggs, poultry, pork, or fish, this sauce blends to make rich marinades and soups.', 'Hoppin'' Hot Sauce', 4.99000, null, null, 'Y', null, null, null, 3);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (4, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'When any pepper is dried and smoked, it is referred to as a Chipotle. Usually with a wrinkled, drak brown appearance, the Chipotle delivers a smokey, sweet flavor which is generally used for adding a smokey, roasted flavor to salsas, stews and marinades.', 'Day of the Dead Chipotle Hot Sauce', 6.99000, null, null, 'Y', null, null, null, 4);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (5, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'If you want hot, this is the chile to choose. Native to the Carribean, Yucatan and Northern Coast of South America, the Habanero presents itself in a variety of colors ranging from light green to a bright red. The Habanero''s bold heat, unique flavor and aroma has made it the favorite of chile lovers.', 'Day of the Dead Habanero Hot Sauce', 6.99000, null, null, 'Y', null, null, null, 5);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (6, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'Often mistaken for the Habanero, the Scotch Bonnet has a deeply inverted tip as opposed to the pointed end of the Habanero. Ranging in many colors from green to yellow-orange, the Scotch Bonnet is a staple in West Indies and Barbados style pepper sauces.', 'Day of the Dead Scotch Bonnet Hot Sauce', 6.99000, null, null, 'Y', null, null, null, 6);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (7, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'Made with Naga Bhut Jolokia, the World''s Hottest pepper.', 'Green Ghost', 11.99000, null, null, 'Y', null, null, null, 7);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (8, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'You bet your boots, this hot sauce earned its name from folks that appreciate an outstanding hot sauce. What you''ll find here is a truly original zesty flavor, not an overpowering pungency that is found in those ordinary Tabasco pepper sauces - even though the pepper used in this product was tested at 285,000 Scoville units. So, saddle up for a ride to remember. To make sure we brought you only the finest Habanero pepper sauce, we went to the foothills of the Mayan mountains in Belize, Central America. This product is prepared entirely by hand using only fresh vegetables and all natural ingredients.', 'Blazin'' Saddle XXX Hot Habanero Pepper Sauce', 4.99000, null, null, 'Y', null, null, null, 8);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (9, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'All Hell is breaking loose, fire &amp; brimstone rain down? prepare to meet your maker.', 'Armageddon The Hot Sauce To End All', 12.99000, null, null, 'Y', null, null, null, 9);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (10, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'Here is the Prescription for those who enjoy intolerable heat. Dr. Chilemeister''s sick and evil deadly brew should be used with caution. Pain can become addictive!', 'Dr. Chilemeister''s Insane Hot Sauce', 12.99000, null, null, 'Y', null, null, null, 10);
INSERT INTO ops$publ.BLC_SKU(SKU_ID, ACTIVE_END_DATE, ACTIVE_START_DATE, AVAILABLE_FLAG, DESCRIPTION, CONTAINER_SHAPE, DEPTH, DIMENSION_UNIT_OF_MEASURE, GIRTH, HEIGHT, CONTAINER_SIZE, WIDTH, DISCOUNTABLE_FLAG, FULFILLMENT_TYPE, INVENTORY_TYPE, IS_MACHINE_SORTABLE, LONG_DESCRIPTION, NAME, RETAIL_PRICE, SALE_PRICE, TAX_CODE, TAXABLE_FLAG, WEIGHT, WEIGHT_UNIT_OF_MEASURE, CURRENCY_CODE, DEFAULT_PRODUCT_ID) VALUES (11, null, CURRENT_TIMESTAMP, null, null, null, null, null, null, null, null, null, 'Y', null, null, null, 'Been there, roped that. Hotter than a buckin'' mare in heat! Sprinkle on meat entrees, seafood and vegetables. Use as additive in barbecue sauce or any food that needs a spicy flavor. Start with a few drops and work up to the desired flavor.', 'Bull Snort Cowboy Cayenne Pepper Hot Sauce', 3.99000, null, null, 'Y', null, null, null, 11);
