INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-74, '12', 'Sku', 'longDescription', 'es', 'Una de las salsas m&aacute;s inusuales que vendemos. La original era un viejo estilo salsa caj&uacute;n y esto versi&oacute;n actualizada ennegrecimiento. Es dulce, pero se obtiene un gran sabor de canela y clavo de olor con un tiro agradable de piacnte de Cayena. Use en todos los alimentos para dar ese toque caj&uacute;n.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-73, '12', 'Sku', 'name', 'es', 'Salsa Dulce de Cajun de Lousiane');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-72, '11', 'Sku', 'longDescription', 'es', 'Espolvorear con platos de carne, pescado y verduras. Use como aditivo en la salsa de barbacoa o cualquier alimento que necesita un sabor picante. Comience con unas gotas y aumente hasta llegar al sabor deseado.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-71, '11', 'Sku', 'name', 'es', 'Salsa Picante del Vaquero');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-70, '10', 'Sku', 'longDescription', 'es', 'Aqu est&aacute; la receta para aquellos que disfrutan de picante intolerable. Esta salsa macabra y mortal del Dr. Chilemeister se debe utilizar con precauci&oacute;n. El dolor puede llegar a ser adictivo!');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-69, '10', 'Sku', 'name', 'es', 'Salsa Loca del Dr. Chilemeister');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-68, '9', 'Sku', 'longDescription', 'es', 'Todo el infierno se ha desatado, fuego y azufre. Est&aacute; listo para el fin?');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-67, '9', 'Sku', 'name', 'es', 'Fin del Mundo Salsa');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-66, '8', 'Sku', 'longDescription', 'es', 'Esta salsa caliente recibe su nombre por la gente que aprecian una salsa picante. Lo que vas a encontrar aqu es un sabor picante realmente original, no una acritud abrumador que se encuentra en las salsas de chile Tabasco ordinarios - a pesar de la pimienta usado en este producto ha sido probado en 285.000 unidades Scoville. Por lo tanto, ensillar a dar un paseo para recordar. Para asegurarnos de que usted trajo s&oacute;lo la mejor salsa de pimiento habanero, nos fuimos a las faldas de las monta&ntilde;as mayas en Belice, Am&eacute;rica Central. Este producto se prepara totalmente a mano utilizando s&oacute;lo las verduras frescas y solo ingredientes naturales.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-65, '8', 'Sku', 'name', 'es', 'Salsa de Habanero de la Silla Ardiente');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-64, '7', 'Sku', 'longDescription', 'es', 'Hecho con Naga Jolokia Bhut, el chile m&aacute;s picante del mundo.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-63, '7', 'Sku', 'name', 'es', 'Fantasma Verde');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-62, '6', 'Sku', 'longDescription', 'es', 'Parecido al Habanero, el Bonnet Escoc&eacute;s tiene una punta profundamente invertidas en comparaci&oacute;n con el extremo puntiagudo del Habanero. Van en colores de verde a amarillo-naranja, el Bonnet Escoc&eacute;s es un alimento b&aacute;sico en West Indies, en Barbados y salsas estilo pimienta.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-61, '6', 'Sku', 'name', 'es', 'Salsa del D&iacute;a de los Muertos de Bonnet Escoc&eacute;s');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-60, '5', 'Sku', 'longDescription', 'es', 'Si quieres picante, este es el Chile a elegir. Originario del Caribe, Yucat&aacute;n y la Costa norte de Am&eacute;rica del Sur, el habanero se presenta en una variedad de colores que van desde el verde claro a un rojo brillante. El calor, sabor y aroma &uacute;nicos del Habanero ha convertido el chile en el favorito de los amantes del picante.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-59, '5', 'Sku', 'name', 'es', 'Salsa del D&iacute;a de los Muertos de Habanero');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-58, '4', 'Sku', 'longDescription', 'es', 'Cuando cualquier pimienta se seca y se fuma, se refiere como un Chipotle. Por lo general, con una apariencia arrugada, caf&eacute; oscuro, el Chipotle ofrece un sabor ahumado y dulce que se utiliza generalmente para agregar un sabor asado a las salsas, guisos y adobos.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-57, '4', 'Sku', 'name', 'es', 'Salsa del D&iacute;a de los Muertos de Chipotle');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-56, '3', 'Sku', 'longDescription', 'es', 'Picante, maduro pimienta que se mezcla junto con el ajo, la cebolla, pasta de tomate y una pizca de az&uacute;car de ca&ntilde;a para hacer de esto una salsa suave. Maravilloso en huevos, aves de corral, carne de cerdo o pescado, esta salsa se mezcla para hacer los adobos y sopas ricas.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-55, '3', 'Sku', 'name', 'es', 'Salsa de la Muerte Saltante');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-54, '2', 'Sku', 'longDescription', 'es', 'El perfecto acompa&ntilde;ante para el pollo, el pescado, hamburguesas o pizza. Una gran mezcla de habanero, mango, fruta de la pasi&oacute;n y mucho m&aacute;s hacen de esta salsa de la Muerte una delicia tropical incre&iacute;ble.');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-53, '2', 'Sku', 'name', 'es', 'Salsa de la Muerte Dulce');
INSERT INTO ops$publ.BLC_TRANSLATION(TRANSLATION_ID, ENTITY_ID, ENTITY_TYPE, FIELD_NAME, LOCALE_CODE, TRANSLATED_VALUE) VALUES (-52, '1', 'Sku', 'longDescription', 'es', 'Como mis amigos salseros saben, nunca soy f&aacute;cil de satisfacer. Por lo tanto, naci&oacute; la creaci&oacute;n de la Muerte S&uacute;bita. Cuando este listo para saborear al m&aacute;s all&aacute; ... Muerte s&uacute;bita entregar&aacute;!');
